def dist_squared(x1: int, y1: int, x2: int, y2: int) -> int:
    """Squared Euclidean distance between two points."""
    dx: int = x2 - x1
    dy: int = y2 - y1
    return dx * dx + dy * dy


def closest_pair_brute(xs: list[int], ys: list[int]) -> int:
    """Find squared distance of closest pair using brute force."""
